using System;using System.Collections.Generic;using UnityEngine;public class GameInitConfig : BaseConfig{    public static bool resLoaded = false;    private static Dictionary<int, GameInitConfig> dic = new Dictionary<int, GameInitConfig>();    public static List<GameInitConfig> array = new List<GameInitConfig>();    public static void Init()    {        LoadRes();    }    public readonly int id;    public readonly string Text;    public GameInitConfig(int id, string Text)    {        this.id = id;        this.Text = Text;    }    private static void OnLoadFile( byte[] data)    { 		ReadStream rs = new ReadStream(data);        /*int file_len = */rs.ReadInt();        string flag = rs.ReadString();        if(flag != "GameInitConfig")        {            LogWarning("invalid file flag" + flag);            return;        }        int col_cnt = rs.ReadShort();        if(col_cnt != 2)        {            LogWarning("col cnt invalid" + col_cnt + " : " + 2);            return;        }        int row_cnt = rs.ReadInt();        for(int i = 0; i < row_cnt; i++)        {            Add_Item(rs);        }        resLoaded = true;    }    private static void Add_Item(ReadStream rs)    {        int id = rs.ReadInt();        string Text = rs.ReadString();        GameInitConfig new_obj_GameInitConfig = new GameInitConfig(id, Text);                if(dic.ContainsKey(id))        {            LogWarning("duplicate key: " + id);            return;        }        dic.Add(id, new_obj_GameInitConfig);        array.Add(new_obj_GameInitConfig);    }    private static void LoadRes()    {        if(resLoaded) return;        byte[] data = GetAsset("GameInitConfig.bytes");		OnLoadFile(data);    }    public static GameInitConfig GetConfig( int id )    {    	GameInitConfig config;    	if ( dic.TryGetValue(id, out config ) )    	{    		return config;    	}    	else    	{    		return null;    	}    }}