using System;using System.Collections.Generic;using UnityEngine;public class IPConfig : BaseConfig{    public static bool resLoaded = false;    private static Dictionary<int, IPConfig> dic = new Dictionary<int, IPConfig>();    public static List<IPConfig> array = new List<IPConfig>();    public static void Init()    {        LoadRes();    }    public readonly int id;    public readonly string name;    public readonly string ip;    public readonly int port;    public readonly string url;    public readonly int type;    public IPConfig(int id, string name, string ip, int port, string url, int type)    {        this.id = id;        this.name = name;        this.ip = ip;        this.port = port;        this.url = url;        this.type = type;    }    private static void OnLoadFile( byte[] data)    { 		ReadStream rs = new ReadStream(data);        /*int file_len = */rs.ReadInt();        string flag = rs.ReadString();        if(flag != "IPConfig")        {            LogWarning("invalid file flag" + flag);            return;        }        int col_cnt = rs.ReadShort();        if(col_cnt != 6)        {            LogWarning("col cnt invalid" + col_cnt + " : " + 6);            return;        }        int row_cnt = rs.ReadInt();        for(int i = 0; i < row_cnt; i++)        {            Add_Item(rs);        }        resLoaded = true;    }    private static void Add_Item(ReadStream rs)    {        int id = rs.ReadInt();        string name = rs.ReadString();        string ip = rs.ReadString();        int port = rs.ReadInt();        string url = rs.ReadString();        int type = rs.ReadInt();        IPConfig new_obj_IPConfig = new IPConfig(id, name, ip, port, url, type);                if(dic.ContainsKey(id))        {            LogWarning("duplicate key: " + id);            return;        }        dic.Add(id, new_obj_IPConfig);        array.Add(new_obj_IPConfig);    }    private static void LoadRes()    {        if(resLoaded) return;        byte[] data = GetAsset("IPConfig.bytes");		OnLoadFile(data);    }    public static IPConfig GetConfig( int id )    {    	IPConfig config;    	if ( dic.TryGetValue(id, out config ) )    	{    		return config;    	}    	else    	{    		return null;    	}    }}