using System;using System.Collections.Generic;using UnityEngine;public class StrConfig : BaseConfig{    public static bool resLoaded = false;    private static Dictionary<int, StrConfig> dic = new Dictionary<int, StrConfig>();    public static List<StrConfig> array = new List<StrConfig>();    public static void Init()    {        LoadRes();    }    public readonly int id;    public readonly string str;    public StrConfig(int id, string str)    {        this.id = id;        this.str = str;    }    private static void OnLoadFile( byte[] data)    { 		ReadStream rs = new ReadStream(data);        /*int file_len = */rs.ReadInt();        string flag = rs.ReadString();        if(flag != "StrConfig")        {            LogWarning("invalid file flag" + flag);            return;        }        int col_cnt = rs.ReadShort();        if(col_cnt != 2)        {            LogWarning("col cnt invalid" + col_cnt + " : " + 2);            return;        }        int row_cnt = rs.ReadInt();        for(int i = 0; i < row_cnt; i++)        {            Add_Item(rs);        }        resLoaded = true;    }    private static void Add_Item(ReadStream rs)    {        int id = rs.ReadInt();        string str = rs.ReadString();        StrConfig new_obj_StrConfig = new StrConfig(id, str);                if(dic.ContainsKey(id))        {            LogWarning("duplicate key: " + id);            return;        }        dic.Add(id, new_obj_StrConfig);        array.Add(new_obj_StrConfig);    }    private static void LoadRes()    {        if(resLoaded) return;        byte[] data = GetAsset("StrConfig.bytes");		OnLoadFile(data);    }    public static StrConfig GetConfig( int id )    {    	StrConfig config;    	if ( dic.TryGetValue(id, out config ) )    	{    		return config;    	}    	else    	{    		return null;    	}    }}